<template>
  <div class="themebox">
    <div class="search-theme__container">
      <!-- テーマ・目的別にホテル・旅館を探す -->
      <div class="title">
        <h2 class="marker-under">{{ theme.list[0].contents_type_ext_col_01 }}</h2>
          <div v-if="theme.pageInfo.totalCnt > 4" class="btn--more pc-only"><a :href="theme.list[0].contents_type_ext_col_02">すべてみる</a></div>
      </div>
      <div class="theme-slider slider-wrap">
        <ssr-carousel
          :slides-per-page="4"
          show-arrows
          :paginate-by-slide="false"
          :peek-right='60'
          :responsive='[
            {
              maxWidth: 768,
              slidesPerPage: 3,
            },
            {
              maxWidth: 428,
              slidesPerPage: 2,
            }
          ]'
        >
          <div
          v-for="(slide, index) in theme.list"
          :key="index"
          class="slide theme-sec-slider__item swiper-slide slider-item-body"
          data-slick-index="0"
          aria-hidden="false"
          tabindex="0"
          >
            <div class="theme-card">
              <div v-if="slide.img.url">
                <!-- 画像があれば画像を表示 -->
                <div class="theme-card__view pc-only">
                  <div class="card-image">
                    <img
                    :src="slide.img.url ?? noImgSrc"
                    :alt="slide.img.desc"
                    class="lazyload"
                    loading="lazy"
                    >
                  </div>
                </div>
                <div class="theme-card__view sp-only">
                  <div class="card-image">
                    <img
                      :src="slide.img_SP.url ?? noImgSrc"
                      :alt="slide.img_SP.desc"
                      class="lazyload"
                      loading="lazy"
                    >
                  </div>
                </div>
              </div>
              <!-- 施設イメージがあれば施設イメージを表示(取得できなかった場合はnoimage表示) -->
              <div v-else-if="slide.hasOwnProperty('shisetsuImage')" class="theme-card__view">
                <div class="card-image">
                  <img
                  :src="slide.shisetsuImage ?? noImgSrc"
                  class="lazyload"
                  loading="lazy"
                  alt=""
                  >
                </div>
              </div>
              <!-- 背景色があれば背景色を表示 -->
              <div v-else-if="slide.backgroungColoer" class="theme-card__view">
                <div class="card-image">
                  <div
                  :style="'background-color:#' + slide.backgroungColoer + ';'"
                  class="lazyload"
                  >
                    <p :style="'color:#' + slide.textColoer + ';'">{{ slide.text }}</p>
                  </div>
                </div>
              </div>
              <!-- 何も取得できなかった場合はnoimage表示 -->
              <div v-else class="theme-card__view">
                <div class="card-image">
                  <img
                  :src="noImgSrc"
                  class="lazyload"
                  loading="lazy"
                  >
                </div>
              </div>
              <div class="theme-card__block">
                <p class="theme-card__title">
                  <a
                    :href="slide.url"
                    :alt="slide.img.desc"
                    class="theme-card__link"
                  >
                    {{ slide.note }}
                  </a>
                </p>
              </div>
            </div>
          </div>
          <template #back-arrow>
            <img alt="" class="ssr-carousel-back-icon" src="data:image/svg+xml;base64,">
          </template>
          <template #next-arrow>
            <img alt="" class="ssr-carousel-next-icon" src="data:image/svg+xml;base64,">
          </template>
        </ssr-carousel>
        <div v-if="theme.pageInfo.totalCnt > 4" class="view-more hotel-sec__more sp-only"><a :href="theme.list[0].contents_type_ext_col_02">すべてみる</a></div>
      </div>
      <hr>
    </div>
    <!-- テーマ・目的別にホテル・旅館を探す ここまで -->
    <!-- おすすめ観光ガイド ここから -->
